  const angle = 365/radius;

  return (
    <View styles={styles.container} >
        {elements&&
            elements.map(
                element => (
                    <TouchableOpacity onClick={element.onClick}>
                        <Image style={styles.icon} source={element.icon} ></Image>
                        <Text style={styles.text}> {element.title} </Text>
                    </TouchableOpacity>
                )
            )
        }
    </View>
)};

